type InitSettings = {
  advancedEdits?: { [key: string]: boolean },
  autocomplete?: boolean,
  completePathname?: string,
  editPathname?: string,
  edits?: boolean,
  endpointHostname?: string,
  headers?:  {[key: string]: string },
  ignoreText?: string,
  isAnonUser?: boolean,
  key?: string,
  lang?: string,
  medical?: boolean,
  mode?: string,
  neuralSpellcheck?: boolean,
  operations?: string[],
  saplingPathPrefix?: string,
  statusBadge?: boolean,
  userId?: string,
  userToggle?: boolean,
  userToggleEnabled?: boolean,
  variety?: string,
}

interface SaplingInterface {
  init(settings: InitSettings): void;
  observe(elem: HTMLElement): void;
  unobserve(elem: HTMLElement): void;
  checkOnce(elem: HTMLElement): void;
}

export const Sapling: SaplingInterface;
